    private void categoryComboBoxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_categoryComboBoxActionPerformed
        // TODO add your handling code here:
        if (categoryComboBox.getSelectedItem().equals("OPONA")){
            brandComboBox.setModel(new javax.swing.DefaultComboBoxModel<>(new String[] { "WYBIERZ", "CONTINENTAL", "DUNLOP", "BRIDGESTONE", "NOKIAN", "FULDA", "MICHELIN" , "DĘBICA" , "DAYTON"  }));
        } else if (categoryComboBox.getSelectedItem().equals("FELGA")) {
            brandComboBox.setModel(new javax.swing.DefaultComboBoxModel<>(new String[] { "WYBIERZ", "DOTZ", "CARMANI", "TSR", "RIAL", "INFINY", "BORBET" , "ATS" , "ALUTEC"  }));
        }
    }//GEN-LAST:event_categoryComboBoxActionPerformed

