    private void addProductButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addProductButtonActionPerformed
        // TODO add your handling code here:
        errorLabel.setText("");
        statusLabel.setText("");
        if (categoryComboBox.getSelectedItem().equals("WYBIERZ")) {
            errorLabel.setText("Wybierz kategorię!");
        } else if (brandComboBox.getSelectedItem().equals("WYBIERZ")) {
            errorLabel.setText("Wybierz markę!");
        } else if (unitsInput.getText().equals("")) {
            errorLabel.setText("Wprowadź ilość!");
        } else {
            statusLabel.setText("Produkt został dodany!");
            unitsInput.setText("");
        } 
    }//GEN-LAST:event_addProductButtonActionPerformed



